package com.yaowang.lansha.entity;import java.io.Serializable;import java.util.Date;/** * 用户抽奖机会  * @author  *  */public class ActivityUser implements Serializable{	private static final long serialVersionUID = 1L;		/**	 * 用户id	 */	private String id;	/**	 * 收件人	 */	private String realname;	/**	 * 收货地址	 */	private String address;	/**	 * 联系电话	 */	private String telphone;	/**	 * qq	 */	private String qq;	/**	 * 上次抽奖时间	 */	private Date lastActivity;	/**	 * create_time	 */	private Date createTime;	/**	 * 抽奖间隔时间(秒)	 */	private Integer limitTime;	/**	 * 设置用户id	 */	 public void setId(String id){	 	this.id = id;	 }	 	 /**	  * 获取用户id	  */	 public String getId(){	 	return this.id;	 }	/**	 * 设置收件人	 */	 public void setRealname(String realname){	 	this.realname = realname;	 }	 	 /**	  * 获取收件人	  */	 public String getRealname(){	 	return this.realname;	 }	/**	 * 设置收货地址	 */	 public void setAddress(String address){	 	this.address = address;	 }	 	 /**	  * 获取收货地址	  */	 public String getAddress(){	 	return this.address;	 }	/**	 * 设置联系电话	 */	 public void setTelphone(String telphone){	 	this.telphone = telphone;	 }	 	 /**	  * 获取联系电话	  */	 public String getTelphone(){	 	return this.telphone;	 }	/**	 * 设置上次抽奖时间	 */	 public void setLastActivity(Date lastActivity){	 	this.lastActivity = lastActivity;	 }	 	 /**	  * 获取上次抽奖时间	  */	 public Date getLastActivity(){	 	return this.lastActivity;	 }		 /**	 * 设置create_time	 */	 public void setCreateTime(Date createTime){	 	this.createTime = createTime;	 }	 	 /**	  * 获取create_time	  */	 public Date getCreateTime(){	 	return this.createTime;	 }	 	 /**	  * 获取间隔时间	  */	 public Integer getLimitTime() {		return limitTime;	}	public void setLimitTime(Integer limitTime) {		this.limitTime = limitTime;	}	public String getQq() {		return qq;	}	public void setQq(String qq) {		this.qq = qq;	}}