package com.yaowang.lansha.entity;import java.io.Serializable;import java.util.Date;/** * 用户抽奖机会  * @author  *  */public class ActivityUserStock implements Serializable{	private static final long serialVersionUID = 1L;		/**	 * id	 */	private String id;	/**	 * 用户id	 */	private String userId;	/**	 * 活动id	 */	private String itemId;	/**	 * 上次抽奖时间	 */	private Date lastActivity;	/**	 * 抽奖次数	 */	private Integer stock;	/**	 * create_time	 */	private Date createTime;	/**	 * 需要增加的抽奖机会数	 */	private Integer add;		/**	 * 抽奖间隔时间(秒)	 */	private Integer limitTime;	/**	 * 设置id	 */	 public void setId(String id){	 	this.id = id;	 }	 	 /**	  * 获取id	  */	 public String getId(){	 	return this.id;	 }	/**	 * 设置用户id	 */	 public void setUserId(String userId){	 	this.userId = userId;	 }	 	 /**	  * 获取用户id	  */	 public String getUserId(){	 	return this.userId;	 }	/**	 * 设置活动id	 */	 public void setItemId(String itemId){	 	this.itemId = itemId;	 }	 	 /**	  * 获取活动id	  */	 public String getItemId(){	 	return this.itemId;	 }	/**	 * 设置上次抽奖时间	 */	 public void setLastActivity(Date lastActivity){	 	this.lastActivity = lastActivity;	 }	 	 /**	  * 获取上次抽奖时间	  */	 public Date getLastActivity(){	 	return this.lastActivity;	 }	/**	 * 设置抽奖次数	 */	 public void setStock(Integer stock){	 	this.stock = stock;	 }	 	 /**	  * 获取抽奖次数	  */	 public Integer getStock(){	 	return this.stock;	 }	/**	 * 设置create_time	 */	 public void setCreateTime(Date createTime){	 	this.createTime = createTime;	 }	 	 /**	  * 获取create_time	  */	 public Date getCreateTime(){	 	return this.createTime;	 }	public Integer getAdd() {		return add;	}	public void setAdd(Integer add) {		this.add = add;	}	/**	  * 获取抽奖间隔时间(秒)	  */	 public Integer getLimitTime() {		return limitTime;	}	public void setLimitTime(Integer limitTime) {		this.limitTime = limitTime;	}}