package com.yaowang.lansha.entity;import java.io.Serializable;import java.util.Date;/** * 精彩活动表  * @author  *  */public class LanshaActivityPush implements Serializable{	private static final long serialVersionUID = 1L;		/**	 * id	 */	private String id;	/**	 * 标题	 */	private String title;	/**	 * 图片	 */	private String indexImg;	/**	 * 活动链接	 */	private String activityUrl;	/**	 * 备注	 */	private String remark;	/**	 * 创建时间	 */	private Date createTime;    private Integer orderId=0;		/**	 * 设置id	 */	 public void setId(String id){	 	this.id = id;	 }	 	 /**	  * 获取id	  */	 public String getId(){	 	return this.id;	 }	/**	 * 设置标题	 */	 public void setTitle(String title){	 	this.title = title;	 }	 	 /**	  * 获取标题	  */	 public String getTitle(){	 	return this.title;	 }	/**	 * 设置图片	 */	 public void setIndexImg(String indexImg){	 	this.indexImg = indexImg;	 }	 	 /**	  * 获取图片	  */	 public String getIndexImg(){	 	return this.indexImg;	 }	/**	 * 设置活动链接	 */	 public void setActivityUrl(String activityUrl){	 	this.activityUrl = activityUrl;	 }	 	 /**	  * 获取活动链接	  */	 public String getActivityUrl(){	 	return this.activityUrl;	 }	/**	 * 设置备注	 */	 public void setRemark(String remark){	 	this.remark = remark;	 }	 	 /**	  * 获取备注	  */	 public String getRemark(){	 	return this.remark;	 }	/**	 * 设置创建时间	 */	 public void setCreateTime(Date createTime){	 	this.createTime = createTime;	 }	 	 /**	  * 获取创建时间	  */	 public Date getCreateTime(){	 	return this.createTime;	 }    public Integer getOrderId() {        return orderId;    }    public void setOrderId(Integer orderId) {        this.orderId = orderId;    }}