package com.yaowang.lansha.service.impl;import java.util.List;import java.util.Map;import javax.annotation.Resource;import org.springframework.stereotype.Service;import com.yaowang.common.dao.PageDto;import com.yaowang.lansha.dao.ActivityItemDao;import com.yaowang.lansha.entity.ActivityItem;import com.yaowang.lansha.service.ActivityItemService;import com.yaowang.util.cache.DefaultCacheManager;import com.yaowang.util.cache.advice.CacheAnnotation;import com.yaowang.util.cache.advice.CacheClearAnnotation;/** * 活动  * @author  *  */@Service("activityItemService")public class ActivityItemServiceImpl extends DefaultCacheManager implements ActivityItemService{	@Resource	private ActivityItemDao activityItemDao;		@Override	@CacheClearAnnotation	public ActivityItem save(ActivityItem entity){		return activityItemDao.save(entity);	}		@Override	@CacheClearAnnotation	public Integer delete(String[] ids){		return activityItemDao.delete(ids);	}		@Override	@CacheClearAnnotation	public Integer update(ActivityItem entity){		return activityItemDao.update(entity);	}	@Override	@CacheClearAnnotation	public void updateStatusById(String id, String status) {		activityItemDao.updateStatusById(id, status);	}		@Override	@CacheAnnotation	public ActivityItem getActivityItemById(String id){		return activityItemDao.getActivityItemById(id);	}		@Override	public ActivityItem getActivityItemByName(String name) {		return activityItemDao.getActivityItemByName(name);	}		@Override	public Map<String, ActivityItem> getActivityItemMapByIds(String[] ids){		return activityItemDao.getActivityItemMapByIds(ids);	}		@Override	public List<ActivityItem> getActivityItemList(ActivityItem entity){		return activityItemDao.getActivityItemList(entity);	}		@Override	public List<ActivityItem> getActivityItemPage(ActivityItem entity, PageDto page){		return activityItemDao.getActivityItemPage(entity, page);	}}