package com.yaowang.lansha.service.impl;import java.util.List;import java.util.Map;import javax.annotation.Resource;import com.yaowang.common.dao.PageDto;import com.yaowang.lansha.dao.ActivityUserDao;import com.yaowang.lansha.entity.ActivityUser;import com.yaowang.lansha.entity.ActivityUserStock;import com.yaowang.lansha.service.ActivityUserService;import com.yaowang.lansha.service.ActivityUserStockService;import org.springframework.stereotype.Service;/** * 用户抽奖机会  * @author  *  */@Service("activityUserService")public class ActivityUserServiceImpl implements ActivityUserService{	@Resource	private ActivityUserDao activityUserDao;	@Resource	private ActivityUserStockService activityUserStockService;		@Override	public ActivityUser save(ActivityUser entity, ActivityUserStock activityUserStock){		if(activityUserStock != null){			activityUserStockService.save(activityUserStock);		}		if(entity != null){			activityUserDao.save(entity);		}		return entity;	}		@Override	public Integer delete(String[] ids){		return activityUserDao.delete(ids);	}		@Override	public Integer update(ActivityUser entity){		return activityUserDao.update(entity);	}		@Override	public Integer updateUserInfo(ActivityUser entity){		return activityUserDao.updateUserInfo(entity);	}		@Override	public ActivityUser getActivityUserById(String id){		return activityUserDao.getActivityUserById(id);	}		@Override	public Map<String, ActivityUser> getActivityUserMapByIds(String[] ids){		return activityUserDao.getActivityUserMapByIds(ids);	}		@Override	public List<ActivityUser> getActivityUserList(ActivityUser entity){		return activityUserDao.getActivityUserList(entity);	}		@Override	public List<ActivityUser> getActivityUserPage(ActivityUser entity, PageDto page){		return activityUserDao.getActivityUserPage(entity, page);	}}