package com.yaowang.lansha.service.impl;import java.util.List;import java.util.Map;import javax.annotation.Resource;import com.yaowang.common.dao.PageDto;import com.yaowang.lansha.dao.LanshaActivityPushDao;import com.yaowang.lansha.entity.LanshaActivityPush;import com.yaowang.lansha.service.LanshaActivityPushService;import org.springframework.stereotype.Service;/** * 精彩活动表  * @author  *  */@Service("lanshaActivityPushService")public class LanshaActivityPushServiceImpl implements LanshaActivityPushService{	@Resource	private LanshaActivityPushDao lanshaActivityPushDao;		@Override	public LanshaActivityPush save(LanshaActivityPush entity){		return lanshaActivityPushDao.save(entity);	}		@Override	public Integer delete(String[] ids){		return lanshaActivityPushDao.delete(ids);	}		@Override	public Integer update(LanshaActivityPush entity){		return lanshaActivityPushDao.update(entity);	}		@Override	public LanshaActivityPush getLanshaActivityPushById(String id){		return lanshaActivityPushDao.getLanshaActivityPushById(id);	}		@Override	public Map<String, LanshaActivityPush> getLanshaActivityPushMapByIds(String[] ids){		return lanshaActivityPushDao.getLanshaActivityPushMapByIds(ids);	}		@Override	public List<LanshaActivityPush> getLanshaActivityPushList(LanshaActivityPush entity){		return lanshaActivityPushDao.getLanshaActivityPushList(entity);	}		@Override	public List<LanshaActivityPush> getLanshaActivityPushPage(LanshaActivityPush entity, PageDto page){		return lanshaActivityPushDao.getLanshaActivityPushPage(entity, page);	}	}