package com.yaowang.lansha.entity;import java.io.Serializable;import java.util.Date;/** * 礼物  * @author  *  */public class LanshaGift implements Serializable{	private static final long serialVersionUID = 1L;		/**	 * id	 */	private String id;	/**	 * 礼物名称	 */	private String name;	/**	 * 0:停用，1:启用	 */	private String status;	/**	 * 1个礼物对应蓝鲨币	 */	private Integer bi;	/**	 * 图标地址	 */	private String img;	/**	 * 排序号	 */	private Integer orderid;	/**	 * 创建时间	 */	private Date createTime;		/**	 * 设置id	 */	 public void setId(String id){	 	this.id = id;	 }	 	 /**	  * 获取id	  */	 public String getId(){	 	return this.id;	 }	/**	 * 设置礼物名称	 */	 public void setName(String name){	 	this.name = name;	 }	 	 /**	  * 获取礼物名称	  */	 public String getName(){	 	return this.name;	 }	/**	 * 设置1个礼物对应蓝鲨币	 */	 public void setBi(Integer bi){	 	this.bi = bi;	 }	 	 /**	  * 获取1个礼物对应蓝鲨币	  */	 public Integer getBi(){	 	return this.bi;	 }	/**	 * 设置图标地址	 */	 public void setImg(String img){	 	this.img = img;	 }	 	 /**	  * 获取图标地址	  */	 public String getImg(){	 	return this.img;	 }	/**	 * 设置排序号	 */	 public void setOrderid(Integer orderid){	 	this.orderid = orderid;	 }	 	 /**	  * 获取排序号	  */	 public Integer getOrderid(){	 	return this.orderid;	 }	/**	 * 设置创建时间	 */	 public void setCreateTime(Date createTime){	 	this.createTime = createTime;	 }	 	 /**	  * 获取创建时间	  */	 public Date getCreateTime(){	 	return this.createTime;	 }	 /**	  * 设置0:停用，1:启用	  */	 public String getStatus() {		return status;	 }	 /**	  * 设置0:停用，1:启用	  * @param status	  */	 public void setStatus(String status) {		this.status = status;	 }}