package com.yaowang.lansha.entity;import java.io.Serializable;/** * 用户财物信息表  * @author  *  */public class YwUserPay implements Serializable{	private static final long serialVersionUID = 1L;		/**	 * 银行卡号	 */	private String cardNo;	/**	 * 用户ID,关联yw_user	 */	private String userId;	/**	 * 开户人姓名	 */	private String name;	/**	 * 开户银行	 */	private String bank;		/**	 * 设置银行卡号	 */	 public void setCardNo(String cardNo){	 	this.cardNo = cardNo;	 }	 	 /**	  * 获取银行卡号	  */	 public String getCardNo(){	 	return this.cardNo;	 }	/**	 * 设置用户ID,关联yw_user	 */	 public void setUserId(String userId){	 	this.userId = userId;	 }	 	 /**	  * 获取用户ID,关联yw_user	  */	 public String getUserId(){	 	return this.userId;	 }	/**	 * 设置开户人姓名	 */	 public void setName(String name){	 	this.name = name;	 }	 	 /**	  * 获取开户人姓名	  */	 public String getName(){	 	return this.name;	 }	/**	 * 设置开户银行	 */	 public void setBank(String bank){	 	this.bank = bank;	 }	 	 /**	  * 获取开户银行	  */	 public String getBank(){	 	return this.bank;	 }}