package com.yaowang.lansha.entity;import com.yaowang.util.DateUtils;import java.io.Serializable;import java.util.Date;/** * 房间日数据  * @author  *  */public class YwUserRoomDayData implements Serializable{	private static final long serialVersionUID = 1L;		/**	 * room_id	 */	private String roomId;    private String userId;	/**	 * 关联lansha_room_ranking	 */	private String rankingId;	/**	 * 当天积分	 */	private Float score;	/**	 * 积分排名	 */	private Integer ranking;	/**	 * 当天收入标准	 */	@SuppressWarnings("unused")	private Float payStandard;	/**	 * 当天关注数	 */	private Integer focusOn;	/**	 * 当天取消关注数	 */	private Integer focusOff;	/**	 * 奖金	 */	private Float bonus;	/**	 * 罚金	 */	private Float forfeit;	/**	 * 日期	 */	private Date day;    private Float salary;    /**     * 直播时长(分钟)     */    private Integer liveTime;    private String mobile;    private String dateTime;    private String playInfo;    private Integer sort;    private String userName;    private String remark;        /**     * @return     * @Description: 格式化直播时长     */    public String getPlayTime() {        return DateUtils.secondToTime(this.getLiveTime() * 60);    }    public Float getLastSalary() {        return this.getSalary()+this.getBonus()-this.getForfeit();    }    public String getUserName() {        return userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    /**	 * 设置room_id	 */	 public void setRoomId(String roomId){	 	this.roomId = roomId;	 }	 	 /**	  * 获取room_id	  */	 public String getRoomId(){	 	return this.roomId;	 }	/**	 * 设置关联lansha_room_ranking	 */	 public void setRankingId(String rankingId){	 	this.rankingId = rankingId;	 }	 	 /**	  * 获取关联lansha_room_ranking	  */	 public String getRankingId(){	 	return this.rankingId;	 }	/**	 * 设置当天积分	 */	 public void setScore(Float score){	 	this.score = score;	 }	 	 /**	  * 获取当天积分	  */	 public Float getScore(){	 	return this.score;	 }	/**	 * 设置积分排名	 */	 public void setRanking(Integer ranking){	 	this.ranking = ranking;	 }	 	 /**	  * 获取积分排名	  */	 public Integer getRanking(){	 	return this.ranking;	 }	/**	 * 设置当天收入标准	 */	 public void setPayStandard(Float payStandard){	 	this.payStandard = payStandard;	 }	 	 /**	  * 获取当天收入标准	  */	 public Float getPayStandard(){	 	return PayStandard.getStandard(this.ranking);	 }	/**	 * 设置当天关注数	 */	 public void setFocusOn(Integer focusOn){	 	this.focusOn = focusOn;	 }	 	 /**	  * 获取当天关注数	  */	 public Integer getFocusOn(){	 	return this.focusOn;	 }	/**	 * 设置当天取消关注数	 */	 public void setFocusOff(Integer focusOff){	 	this.focusOff = focusOff;	 }	 	 /**	  * 获取当天取消关注数	  */	 public Integer getFocusOff(){	 	return this.focusOff;	 }	/**	 * 设置奖金	 */	 public void setBonus(Float bonus){	 	this.bonus = bonus;	 }	 	 /**	  * 获取奖金	  */	 public Float getBonus(){	 	return this.bonus;	 }	/**	 * 设置罚金	 */	 public void setForfeit(Float forfeit){	 	this.forfeit = forfeit;	 }	 	 /**	  * 获取罚金	  */	 public Float getForfeit(){	 	return this.forfeit;	 }	/**	 * 设置日期	 */	 public void setDay(Date day){	 	this.day = day;	 }	 	 /**	  * 获取日期	  */	 public Date getDay(){	 	return this.day;	 }    public Float getSalary() {        return salary;    }    public void setSalary(Float salary) {        this.salary = salary;    }    public String getUserId() {        return userId;    }    public void setUserId(String userId) {        this.userId = userId;    }    public Integer getLiveTime() {        return liveTime;    }    public void setLiveTime(Integer liveTime) {        this.liveTime = liveTime;    }    public String getMobile() {        return mobile;    }    public void setMobile(String mobile) {        this.mobile = mobile;    }    public String getDateTime() {        return dateTime;    }    public void setDateTime(String dateTime) {        this.dateTime = dateTime;    }    public String getPlayInfo() {        return playInfo;    }    public void setPlayInfo(String playInfo) {        this.playInfo = playInfo;    }    public Integer getSort() {        return sort;    }    public void setSort(Integer sort) {        this.sort = sort;    }    public String getRemark() {        return remark;    }    public void setRemark(String remark) {        this.remark = remark;    }}enum PayStandard{	L1(1,5,30.00f),	L2(6,50,20.00f),	L3(51,100,15.00f),	L4(101,200,10.00f),	L5(201,300,5.00f),	L6(301,-1,3.00f),	Others(0,0,0.00f);		int start;	int end;	Float money;		PayStandard(int start,int end,Float money){		this.start = start;		this.end = end;		this.money = money;	}	public int getStart() {		return start;	}	public void setStart(int start) {		this.start = start;	}	public int getEnd() {		return end;	}	public void setEnd(int end) {		this.end = end;	}	public Float getMoney() {		return money;	}	public void setMoney(Float money) {		this.money = money;	}		public static Float getStandard(int rank){		PayStandard[] sts = PayStandard.values();		for(PayStandard standard : sts){			if(standard.getStart() <= rank && (standard.getEnd() >= rank || standard.getEnd() == -1)){				return standard.getMoney();			}		}		return Others.getMoney();	}}