package com.yaowang.lansha.service.impl;import java.util.List;import java.util.Map;import javax.annotation.Resource;import com.yaowang.common.dao.PageDto;import com.yaowang.lansha.dao.LanshaImGiveLogDao;import com.yaowang.lansha.entity.LanshaImGiveLog;import com.yaowang.lansha.service.LanshaImGiveLogService;import org.springframework.stereotype.Service;/** * 发言得虾米记录  * @author  *  */@Service("lanshaImGiveLogService")public class LanshaImGiveLogServiceImpl implements LanshaImGiveLogService{	@Resource	private LanshaImGiveLogDao lanshaImGiveLogDao;		@Override	public LanshaImGiveLog save(LanshaImGiveLog entity){		return lanshaImGiveLogDao.save(entity);	}		@Override	public Integer delete(String[] ids){		return lanshaImGiveLogDao.delete(ids);	}		@Override	public Integer update(LanshaImGiveLog entity){		return lanshaImGiveLogDao.update(entity);	}		@Override	public LanshaImGiveLog getLanshaImGiveLogById(String id){		return lanshaImGiveLogDao.getLanshaImGiveLogById(id);	}		@Override	public Map<String, LanshaImGiveLog> getLanshaImGiveLogMapByIds(String[] ids){		return lanshaImGiveLogDao.getLanshaImGiveLogMapByIds(ids);	}		@Override	public List<LanshaImGiveLog> getLanshaImGiveLogList(LanshaImGiveLog entity){		return lanshaImGiveLogDao.getLanshaImGiveLogList(entity);	}		@Override	public List<LanshaImGiveLog> getLanshaImGiveLogPage(LanshaImGiveLog entity, PageDto page){		return lanshaImGiveLogDao.getLanshaImGiveLogPage(entity, page);	}		@Override	public Integer getGiveNumber(LanshaImGiveLog entity) {		return lanshaImGiveLogDao.getGiveNumber(entity);	}}