package com.yaowang.lansha.service.impl;import java.util.Date;import java.util.List;import java.util.Map;import javax.annotation.Resource;import org.springframework.stereotype.Service;import com.yaowang.common.dao.PageDto;import com.yaowang.lansha.dao.LanshaUserRecordDao;import com.yaowang.lansha.entity.LanshaUserRecord;import com.yaowang.lansha.service.LanshaUserRecordService;/** * 用户收支记录  * @author  *  */@Service("lanshaUserRecordService")public class LanshaUserRecordServiceImpl implements LanshaUserRecordService{	@Resource	private LanshaUserRecordDao lanshaUserRecordDao;		@Override	public LanshaUserRecord save(LanshaUserRecord entity){		return lanshaUserRecordDao.save(entity);	}		@Override	public Integer delete(String[] ids){		return lanshaUserRecordDao.delete(ids);	}		@Override	public Integer update(LanshaUserRecord entity){		return lanshaUserRecordDao.update(entity);	}		@Override	public LanshaUserRecord getLanshaUserRecordById(String id){		return lanshaUserRecordDao.getLanshaUserRecordById(id);	}		@Override	public Map<String, LanshaUserRecord> getLanshaUserRecordMapByIds(String[] ids){		return lanshaUserRecordDao.getLanshaUserRecordMapByIds(ids);	}		@Override	public List<LanshaUserRecord> getLanshaUserRecordList(LanshaUserRecord entity){		return lanshaUserRecordDao.getLanshaUserRecordList(entity);	}		@Override	public List<LanshaUserRecord> getLanshaUserRecordPage(LanshaUserRecord entity, PageDto page, Date startTime, Date endTime){		return lanshaUserRecordDao.getLanshaUserRecordPage(entity, page, startTime, endTime);	}	@Override	public Integer saveBatch(List<LanshaUserRecord> list) {		return lanshaUserRecordDao.saveBatch(list);	}	}