package com.yaowang.lansha.service.impl;import java.util.List;import java.util.Map;import javax.annotation.Resource;import org.springframework.stereotype.Service;import com.yaowang.common.dao.PageDto;import com.yaowang.lansha.dao.WechatAccessTokenDao;import com.yaowang.lansha.entity.WechatAccessToken;import com.yaowang.lansha.service.WechatAccessTokenService;import com.yaowang.util.cache.DefaultCacheManager;import com.yaowang.util.cache.advice.CacheAnnotation;import com.yaowang.util.cache.advice.CacheClearAnnotation;/** * 微信接口访问的Token  * @author  *  */@Service("wechatAccessTokenService")public class WechatAccessTokenServiceImpl extends DefaultCacheManager implements WechatAccessTokenService{	@Resource	private WechatAccessTokenDao wechatAccessTokenDao;		@Override	@CacheClearAnnotation	public WechatAccessToken save(WechatAccessToken entity){		return wechatAccessTokenDao.save(entity);	}		@Override	@CacheClearAnnotation	public Integer delete(String[] ids){		return wechatAccessTokenDao.delete(ids);	}		@Override	@CacheClearAnnotation	public Integer update(WechatAccessToken entity){		return wechatAccessTokenDao.update(entity);	}		@Override	@CacheAnnotation	public WechatAccessToken getWechatAccessTokenById(String id){		return wechatAccessTokenDao.getWechatAccessTokenById(id);	}		@Override	@CacheAnnotation	public WechatAccessToken getWechatAccessTokenByAppId(String appId){		return wechatAccessTokenDao.getWechatAccessTokenByAppId(appId);	}		@Override	@CacheAnnotation	public Map<String, WechatAccessToken> getWechatAccessTokenMapByIds(String[] ids){		return wechatAccessTokenDao.getWechatAccessTokenMapByIds(ids);	}		@Override	@CacheAnnotation	public List<WechatAccessToken> getWechatAccessTokenList(WechatAccessToken entity){		return wechatAccessTokenDao.getWechatAccessTokenList(entity);	}		@Override	@CacheAnnotation	public List<WechatAccessToken> getWechatAccessTokenPage(WechatAccessToken entity, PageDto page){		return wechatAccessTokenDao.getWechatAccessTokenPage(entity, page);	}	}