package com.yaowang.lansha.service.impl;import java.util.List;import java.util.Map;import javax.annotation.Resource;import org.springframework.stereotype.Service;import com.yaowang.common.dao.PageDto;import com.yaowang.lansha.dao.WechatJsapiticketDao;import com.yaowang.lansha.entity.WechatJsapiticket;import com.yaowang.lansha.service.WechatJsapiticketService;import com.yaowang.util.cache.DefaultCacheManager;import com.yaowang.util.cache.advice.CacheAnnotation;import com.yaowang.util.cache.advice.CacheClearAnnotation;/** * 微信JS接口访问的票据  * @author  *  */@Service("wechatJsapiticketService")public class WechatJsapiticketServiceImpl extends DefaultCacheManager implements WechatJsapiticketService{	@Resource	private WechatJsapiticketDao wechatJsapiticketDao;		@Override	@CacheClearAnnotation	public WechatJsapiticket save(WechatJsapiticket entity){		return wechatJsapiticketDao.save(entity);	}		@Override	@CacheClearAnnotation	public Integer delete(String[] ids){		return wechatJsapiticketDao.delete(ids);	}		@Override	@CacheClearAnnotation	public Integer update(WechatJsapiticket entity){		return wechatJsapiticketDao.update(entity);	}		@Override	@CacheAnnotation	public WechatJsapiticket getWechatJsapiticketById(String id){		return wechatJsapiticketDao.getWechatJsapiticketById(id);	}		@Override	@CacheAnnotation	public WechatJsapiticket getWechatJsapiticketByAppId(String appId){		return wechatJsapiticketDao.getWechatJsapiticketByAppId(appId);	}		@Override	@CacheAnnotation	public Map<String, WechatJsapiticket> getWechatJsapiticketMapByIds(String[] ids){		return wechatJsapiticketDao.getWechatJsapiticketMapByIds(ids);	}		@Override	@CacheAnnotation	public List<WechatJsapiticket> getWechatJsapiticketList(WechatJsapiticket entity){		return wechatJsapiticketDao.getWechatJsapiticketList(entity);	}		@Override	@CacheAnnotation	public List<WechatJsapiticket> getWechatJsapiticketPage(WechatJsapiticket entity, PageDto page){		return wechatJsapiticketDao.getWechatJsapiticketPage(entity, page);	}	}