package com.yaowang.lansha.service.impl;import java.util.List;import java.util.Map;import javax.annotation.Resource;import org.springframework.stereotype.Service;import com.yaowang.common.dao.PageDto;import com.yaowang.lansha.dao.YwGameTypeDao;import com.yaowang.lansha.entity.YwGameType;import com.yaowang.lansha.service.YwGameTypeService;import com.yaowang.util.cache.DefaultCacheManager;import com.yaowang.util.cache.advice.CacheAnnotation;import com.yaowang.util.cache.advice.CacheClearAnnotation;/** * yw_game_type  * @author  *  */@Service("ywGameTypeService")public class YwGameTypeServiceImpl extends DefaultCacheManager implements YwGameTypeService{	@Resource	private YwGameTypeDao ywGameTypeDao;		@Override	@CacheClearAnnotation	public YwGameType save(YwGameType entity){		return ywGameTypeDao.save(entity);	}		@Override	@CacheClearAnnotation	public Integer delete(String[] ids){		return ywGameTypeDao.delete(ids);	}	@Override	@CacheClearAnnotation	public Integer updateStatus(String[] ids, Integer status) {		return ywGameTypeDao.updateStatus(ids, status);	}	@Override	@CacheClearAnnotation	public Integer update(YwGameType entity) {		return ywGameTypeDao.update(entity);	}		@Override	public YwGameType getYwGameTypeById(String id){		return ywGameTypeDao.getYwGameTypeById(id);	}		@Override	@CacheAnnotation	public Map<String, YwGameType> getYwGameTypeMapByIds(String[] ids){		return ywGameTypeDao.getYwGameTypeMapByIds(ids);	}		@Override	public List<YwGameType> getYwGameTypeList(){		return ywGameTypeDao.getYwGameTypeList();	}		@Override	public List<YwGameType> getYwGameTypeList(YwGameType entity, PageDto page){		return ywGameTypeDao.getYwGameTypeList(entity, page);	}}