package com.yaowang.lansha.service.impl;import java.util.List;import java.util.Map;import javax.annotation.Resource;import com.yaowang.common.dao.PageDto;import com.yaowang.lansha.dao.YwRoomMonthSettlementDao;import com.yaowang.lansha.entity.YwRoomMonthSettlement;import com.yaowang.lansha.service.YwRoomMonthSettlementService;import org.springframework.stereotype.Service;/** * 房间月结  * @author  *  */@Service("ywRoomMonthSettlementService")public class YwRoomMonthSettlementServiceImpl implements YwRoomMonthSettlementService{	@Resource	private YwRoomMonthSettlementDao ywRoomMonthSettlementDao;		@Override	public YwRoomMonthSettlement save(YwRoomMonthSettlement entity){		return ywRoomMonthSettlementDao.save(entity);	}		@Override	public Integer delete(String[] roomIds){		return ywRoomMonthSettlementDao.delete(roomIds);	}		@Override	public Integer update(YwRoomMonthSettlement entity){		return ywRoomMonthSettlementDao.update(entity);	}		@Override	public YwRoomMonthSettlement getYwRoomMonthSettlementById(String roomId){		return ywRoomMonthSettlementDao.getYwRoomMonthSettlementById(roomId);	}		@Override	public Map<String, YwRoomMonthSettlement> getYwRoomMonthSettlementMapByIds(String[] roomIds){		return ywRoomMonthSettlementDao.getYwRoomMonthSettlementMapByIds(roomIds);	}		@Override	public List<YwRoomMonthSettlement> getYwRoomMonthSettlementList(YwRoomMonthSettlement entity){		return ywRoomMonthSettlementDao.getYwRoomMonthSettlementList(entity);	}		@Override	public List<YwRoomMonthSettlement> getYwRoomMonthSettlementPage(YwRoomMonthSettlement entity, PageDto page){		return ywRoomMonthSettlementDao.getYwRoomMonthSettlementPage(entity, page);	}	}