package com.yaowang.lansha.service.impl;import java.util.List;import java.util.Map;import javax.annotation.Resource;import com.yaowang.common.dao.PageDto;import com.yaowang.lansha.dao.YwUserApplyDao;import com.yaowang.lansha.entity.YwUserApply;import com.yaowang.lansha.service.YwUserApplyService;import org.springframework.stereotype.Service;/** * 热门推荐房间  * @author  *  */@Service("ywUserApplyService")public class YwUserApplyServiceImpl implements YwUserApplyService{	@Resource	private YwUserApplyDao ywUserApplyDao;		@Override	public YwUserApply save(YwUserApply entity){		return ywUserApplyDao.save(entity);	}		@Override	public Integer delete(String[] ids){		return ywUserApplyDao.delete(ids);	}		@Override	public Integer update(YwUserApply entity){		return ywUserApplyDao.update(entity);	}		@Override	public YwUserApply getYwUserApplyById(String id){		return ywUserApplyDao.getYwUserApplyById(id);	}		@Override	public Map<String, YwUserApply> getYwUserApplyMapByIds(String[] ids){		return ywUserApplyDao.getYwUserApplyMapByIds(ids);	}		@Override	public List<YwUserApply> getYwUserApplyList(YwUserApply entity){		return ywUserApplyDao.getYwUserApplyList(entity);	}		@Override	public List<YwUserApply> getYwUserApplyPage(YwUserApply entity, PageDto page){		return ywUserApplyDao.getYwUserApplyPage(entity, page);	}	}