package com.yaowang.lansha.service.impl;import java.util.ArrayList;import java.util.List;import java.util.Map;import javax.annotation.Resource;import com.yaowang.common.dao.PageDto;import com.yaowang.lansha.dao.YwUserPayDao;import com.yaowang.lansha.entity.YwUserPay;import com.yaowang.lansha.service.YwUserPayService;import org.apache.commons.lang3.StringUtils;import org.springframework.stereotype.Service;/** * 用户财物信息表  * @author  *  */@Service("ywUserPayService")public class YwUserPayServiceImpl implements YwUserPayService{	@Resource	private YwUserPayDao ywUserPayDao;		@Override	public YwUserPay save(YwUserPay entity){		return ywUserPayDao.save(entity);	}		@Override	public Integer delete(String[] cardNos){		return ywUserPayDao.delete(cardNos);	}		@Override	public Integer update(YwUserPay entity){		return ywUserPayDao.update(entity);	}		@Override	public YwUserPay getYwUserPayById(String cardNo){		return ywUserPayDao.getYwUserPayById(cardNo);	}		@Override	public Map<String, YwUserPay> getYwUserPayMapByIds(String[] cardNos){		return ywUserPayDao.getYwUserPayMapByIds(cardNos);	}		@Override	public List<YwUserPay> getYwUserPayList(YwUserPay entity){		return ywUserPayDao.getYwUserPayList(entity);	}		@Override	public List<YwUserPay> getYwUserPayPage(YwUserPay entity, PageDto page){		return ywUserPayDao.getYwUserPayPage(entity, page);	}	@Override	public List<YwUserPay> getYwUserPayByUserId(String userId) {		if(StringUtils.isBlank(userId)){			return new ArrayList<YwUserPay>(0);		}		YwUserPay query = new YwUserPay();		query.setUserId(userId);		return ywUserPayDao.getYwUserPayList(query);	}	}