ate getCreateTime() {		return this.createTime;	}	/**	 * 设置最后登录时间	 */	public void setLoginTime(Date loginTime) {		this.loginTime = loginTime;	}	/**	 * 获取最后登录时间	 */	public Date getLoginTime() {		return this.loginTime;	}	/**	 * 设置最后登录ip	 */	public void setLoginIp(String loginIp) {		this.loginIp = loginIp;	}	/**	 * 获取最后登录ip	 */	public String getLoginIp() {		return this.loginIp;	}//	//	/**//	 * 加密密码//	 * //	 * @return//	 *///	public String getEncryptPassword() {//		if (StringUtils.isEmpty(password)) {//			return "";//		}else {//			return MD5.encrypt(password);//		}//	}	/**	 * 设置加密密码	 * @param password	 * @return	 */	public void setEncryptPassword(String password){		this.password = MD5.encrypt(password);	}	public Boolean equalsPwd(String pwd) {		if (StringUtils.isEmpty(pwd) && StringUtils.isEmpty(password)) {			return true;		}		pwd = MD5.encrypt(pwd);		return pwd.equals(password);	}	public Boolean getIsSys() {		return isSys;	}	public void setIsSys(Boolean isSys) {		this.isSys = isSys;	}	public String getEmail() {		return email;	}	public void setEmail(String email) {		this.email = email;	}	public String getTelphone() {		return telphone;	}	public void setTelphone(String telphone) {		this.telphone = telphone;	}	public String getRealname() {		return realname;	}	public void setRealname(String realname) {		this.realname = realname;	}	public Map<String, Object> getData() {		return data;	}	public void setData(Map<String, Object> data) {		this.data = data;	}	public int getProcessCounts() {		return processCounts;	}	public void setProcessCounts(int processCounts) {		this.processCounts = processCounts;	}	@Override	public int compareTo(AdminUser o) {        if (this.processCounts > o.getProcessCounts()) {            return 1;        }else {            return -1;        }	}	@Override	public boolean equals(Object obj) {		return ((AdminUser)obj).getUsername().equals(this.getUsername());	}	public int getOrderNum() {		return orderNum;	}	public void setOrderNum(int orderNum) {		this.orderNum = orderNum;	}	}