package com.yaowang.entity;import java.io.Serializable;import java.util.Date;/** * log_email  * @author  *  */public class LogEmail implements Serializable{	private static final long serialVersionUID = 1L;		/**	 * id	 */	private Integer id;	/**	 * 邮箱	 */	private String email;	/**	 * 发送时间	 */	private Date time;	/**	 * 标题	 */	private String title;	/**	 * 发送内容	 */	private String content;	/**	 * 错误消息	 */	private String error;	/**	 * 状态（0：有效，1：已验证，2：发送失败）	 */	private String status;		/**	 * 设置id	 */	 public void setId(Integer id){	 	this.id = id;	 }	 	 /**	  * 获取id	  */	 public Integer getId(){	 	return this.id;	 }	/**	 * 设置邮箱	 */	 public void setEmail(String email){	 	this.email = email;	 }	 	 /**	  * 获取邮箱	  */	 public String getEmail(){	 	return this.email;	 }	/**	 * 设置发送时间	 */	 public void setTime(Date time){	 	this.time = time;	 }	 	 /**	  * 获取发送时间	  */	 public Date getTime(){	 	return this.time;	 }	/**	 * 设置标题	 */	 public void setTitle(String title){	 	this.title = title;	 }	 	 /**	  * 获取标题	  */	 public String getTitle(){	 	return this.title;	 }	/**	 * 设置发送内容	 */	 public void setContent(String content){	 	this.content = content;	 }	 	 /**	  * 获取发送内容	  */	 public String getContent(){	 	return this.content;	 }	/**	 * 设置错误消息	 */	 public void setError(String error){	 	this.error = error;	 }	 	 /**	  * 获取错误消息	  */	 public String getError(){	 	return this.error;	 }	/**	 * 设置状态（0：有效，1：已验证，2：发送失败）	 */	 public void setStatus(String status){	 	this.status = status;	 }	 	 /**	  * 获取状态（0：有效，1：已验证，2：发送失败）	  */	 public String getStatus(){	 	return this.status;	 }}