package com.yaowang.entity;import java.io.Serializable;import org.apache.commons.lang.StringUtils;import com.yaowang.util.Html2Text;/** * sys_option  * @author  *  */public class SysOption implements Serializable{	private static final long serialVersionUID = 1L;		/**	 * id	 */	private String id;	/**	 * iniid	 */	private String iniid;	/**	 * name	 */	private String name;	/**	 * dvalue	 */	private String dvalue;	/**	 * nowvalue	 */	private String nowvalue;	/**	 * description	 */	private String description;	/**	 * viewable	 */	private Integer viewable;	/**	 * orderid	 */	private Integer orderid;	/**	 * validatejs	 */	private String validatejs;	/**	 * type_id	 */	private String typeId;		/**	 * 设置id	 */	 public void setId(String id){	 	this.id = id;	 }	 	 /**	  * 获取id	  */	 public String getId(){	 	return this.id;	 }	/**	 * 设置iniid	 */	 public void setIniid(String iniid){	 	this.iniid = iniid;	 }	 	 /**	  * 获取iniid	  */	 public String getIniid(){	 	return this.iniid;	 }	/**	 * 设置name	 */	 public void setName(String name){	 	this.name = name;	 }	 	 /**	  * 获取name	  */	 public String getName(){	 	return this.name;	 }	/**	 * 设置dvalue	 */	 public void setDvalue(String dvalue){	 	this.dvalue = dvalue;	 }	 	 /**	  * 获取dvalue	  */	 public String getDvalue(){	 	return this.dvalue;	 }	/**	 * 设置nowvalue	 */	 public void setNowvalue(String nowvalue){	 	this.nowvalue = nowvalue;	 }	 	 /**	  * 获取nowvalue	  */	 public String getNowvalue(){	 	return this.nowvalue;	 }	 /**	  * 过来html标签	  * @return	  */	 public String getValue(){		 if (StringUtils.isEmpty(nowvalue)) {			return "";		 }		 String html = Html2Text.html2Text(nowvalue);		 return html;	 }	 	/**	 * 设置description	 */	 public void setDescription(String description){	 	this.description = description;	 }	 	 /**	  * 获取description	  */	 public String getDescription(){	 	return this.description;	 }	/**	 * 设置viewable	 */	 public void setViewable(Integer viewable){	 	this.viewable = viewable;	 }	 	 /**	  * 获取viewable	  */	 public Integer getViewable(){	 	return this.viewable;	 }	/**	 * 设置orderid	 */	 public void setOrderid(Integer orderid){	 	this.orderid = orderid;	 }	 	 /**	  * 获取orderid	  */	 public Integer getOrderid(){	 	return this.orderid;	 }	/**	 * 设置validatejs	 */	 public void setValidatejs(String validatejs){	 	this.validatejs = validatejs;	 }	 	 /**	  * 获取validatejs	  */	 public String getValidatejs(){	 	return this.validatejs;	 }	/**	 * 设置type_id	 */	 public void setTypeId(String typeId){	 	this.typeId = typeId;	 }	 	 /**	  * 获取type_id	  */	 public String getTypeId(){	 	return this.typeId;	 }}