package com.yaowang.service.impl;import java.util.List;import java.util.Map;import javax.annotation.Resource;import org.springframework.stereotype.Service;import com.yaowang.common.dao.PageDto;import com.yaowang.dao.AdminUserDao;import com.yaowang.entity.AdminUser;import com.yaowang.service.AdminUserService;import com.yaowang.util.MD5;/** * 管理员用户表  * @author  *  */@Service("adminUserService")public class AdminUserServiceImpl implements AdminUserService{	@Resource	private AdminUserDao adminUserDao;		@Override	public AdminUser save(AdminUser adminUser){		return adminUserDao.save(adminUser);	}		@Override	public Integer delete(String[] ids){		return adminUserDao.delete(ids);	}		@Override	public Integer update(AdminUser adminUser){		return adminUserDao.update(adminUser);	}		@Override	public Integer updatePwd(AdminUser user) {		return adminUserDao.updatePwd(user);	}		@Override	public AdminUser getAdminUserById(String id){		return adminUserDao.getAdminUserById(id);	}		@Override	public AdminUser getAdminUserByUserName(String userName) {		return adminUserDao.getAdminUserByUserName(userName);	}		@Override	public Map<String, AdminUser> getAdminUserMapByIds(String[] ids){		return adminUserDao.getAdminUserMapByIds(ids);	}		@Override	public List<AdminUser> getAdminUserList(String id){		return adminUserDao.getAdminUserList(id);	}		@Override	public List<AdminUser> getAdminUserPage(PageDto page){		return adminUserDao.getAdminUserPage(page);	}	@Override	public List<AdminUser> getWaiterList() {		return adminUserDao.getAdminUserList();	}		@Override	public Integer updatePwd(String[] ids) {		String defaultPwd = MD5.encrypt("123456");		return adminUserDao.updatePwd(ids,defaultPwd);	}	@Override	public List<AdminUser> getAdminUserListByName(AdminUser entity) {		return adminUserDao.getAdminUserListByName(entity);	}}