package com.yaowang.service.impl;import java.util.Date;import java.util.List;import java.util.Map;import javax.annotation.Resource;import org.springframework.stereotype.Service;import com.yaowang.common.dao.PageDto;import com.yaowang.dao.LogEmailDao;import com.yaowang.entity.LogEmail;import com.yaowang.service.LogEmailService;/** * log_email  * @author  *  */@Service("logEmailService")public class LogEmailServiceImpl implements LogEmailService{	@Resource	private LogEmailDao logEmailDao;		@Override	public LogEmail save(LogEmail logEmail){		return logEmailDao.save(logEmail);	}		@Override	public Integer delete(Integer[] ids){		return logEmailDao.delete(ids);	}		@Override	public Integer update(LogEmail logEmail){		return logEmailDao.update(logEmail);	}		@Override	public LogEmail getLogEmailById(Integer id){		return logEmailDao.getLogEmailById(id);	}		@Override	public Map<Integer, LogEmail> getLogEmailMapByIds(Integer[] ids){		return logEmailDao.getLogEmailMapByIds(ids);	}		@Override	public List<LogEmail> getLogEmailList(){		return logEmailDao.getLogEmailList();	}		@Override	public List<LogEmail> getLogEmailPage(Date startTime, Date endTime, PageDto page){		return logEmailDao.getLogEmailPage(startTime, endTime, page);	}	@Override	public void updateLogEmailByCondition(String email, String title) {		logEmailDao.updateLogEmailByCondition(email, title);	}	}