package com.yaowang.service.impl;import java.util.Date;import java.util.List;import java.util.Map;import javax.annotation.Resource;import org.springframework.stereotype.Service;import com.yaowang.common.dao.PageDto;import com.yaowang.dao.LogMtDao;import com.yaowang.entity.LogMt;import com.yaowang.service.LogMtService;/** * log_mt  * @author  *  */@Service("logMtService")public class LogMtServiceImpl implements LogMtService{	@Resource	private LogMtDao logMtDao;		@Override	public LogMt save(LogMt logMt){		return logMtDao.save(logMt);	}		@Override	public Integer delete(Integer[] ids){		return logMtDao.delete(ids);	}		@Override	public Integer update(LogMt logMt){		return logMtDao.update(logMt);	}		@Override	public Integer updateStatus(LogMt logMt) {		return logMtDao.updateStatus(logMt);	}		@Override	public LogMt getLogMtById(Integer id){		return logMtDao.getLogMtById(id);	}		@Override	public Map<Integer, LogMt> getLogMtMapByIds(Integer[] ids){		return logMtDao.getLogMtMapByIds(ids);	}		@Override	public List<LogMt> getLogMtList(){		return logMtDao.getLogMtList();	}		@Override	public List<LogMt> getLogMtPage(LogMt mt,Date startTime,Date endTime,PageDto page){		return logMtDao.getLogMtPage(mt, startTime, endTime,page);	}	@Override	public LogMt getLogMtByTelphone(String telphone) {		return logMtDao.getLogMtByTelphone(telphone);	}		@Override	public Integer getLogCount(LogMt entity, Date startTime, Date endTime) {		return logMtDao.getLogCount(entity, startTime, endTime);	}}